script:
frappe.ui.form.on('Material Request', {
    on_submit: function(frm) {
        if (frm.doc.material_request_type === "Purchase") {
            frappe.call({
                method: 'vontoc.api.material_request.material_request_submitted',
                args: {
                    docname: frm.doc.name
                },
                callback: function(r) {
                    if (!r.exc) {
                        frappe.msgprint(__('å·²å¤„ç†é‡‡è´­ç”³è¯·ç›¸å…³é€»è¾‘'));
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Purchase Order', {
	after_workflow_action: function(frm) {
		if (frm.doc.workflow_state === 'Sent For Approval'){
		    frappe.call({
                method: "vontoc.api.purchase_order.sent_po_for_approval",
                args: {
                    docname: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
		}
	}
})

frappe.ui.form.on('Purchase Receipt', {
    after_workflow_action: async function(frm) {
        if (frm.doc.workflow_state !== 'Pending Warehouse Receipt') return;

        const promises = frm.doc.items.map(row => {
            return frappe.db.get_value('Item', row.item_code, 'inspection_required_before_purchase')
                .then(r => r.message.inspection_required_before_purchase);
        });

        const results = await Promise.all(promises);
        const has_inspection_required = results.includes(1);

        // ðŸ” æ— è®ºæ˜¯å¦éœ€è¦è´¨æ£€ï¼Œéƒ½è°ƒç”¨ Python åŽç«¯æ–¹æ³•ï¼Œé€šè¿‡å˜é‡has_inspection_requiredåœ¨åŽç«¯åšåˆ¤æ–­
        frappe.call({
            method: "vontoc.api.purchase_receipt.item_quality_inspection_or_not",
            args: {
                docname: frm.doc.name,
                has_inspection_required: has_inspection_required
            },
            callback: function(r) {
                if (r.message) {
                    frappe.msgprint(__(r.message));
                }
            }
        });
    }
});

frappe.ui.form.on('Purchase Receipt', {
    refresh: function(frm) {
        // åªæœ‰ inspection ç”¨æˆ· ä¸”æœªç¡®è®¤è¿‡æ‰æ˜¾ç¤ºæŒ‰é’®
        frappe.call({
            method: "vontoc.utils.utils.get_user_profile_name", // æ›¿æ¢ä¸ºä½ çš„æ–¹æ³•
            callback: function(r) {
                if (
                    r.message === "inspection" &&
                    !frm.doc.custom_inspection_confirmed
                ) {
                    frm.add_custom_button("è´¨æ£€ç¡®è®¤", () => {
                        // æŒ‰é’®ç‚¹å‡»é€»è¾‘
                        frappe.call({
                            method: "vontoc.api.purchase_receipt.quality_inspection_finished",
                            args: {
                                docname: frm.doc.name
                            },
                            callback: function() {
                                frappe.msgprint("è´¨æ£€å·²ç¡®è®¤");
                                frm.reload_doc(); // åˆ·æ–°è¡¨å•ä»¥éšè—æŒ‰é’®
                            }
                        });
                    });
                }
            }
        });
    }
});

frappe.ui.form.on('Delivery Note', {
    after_workflow_action: function(frm) {
        let state = frm.doc.workflow_state;
        console.log("å½“å‰çŠ¶æ€æ˜¯: ", state);
        if (state === 'Sent For Approval') {
            frappe.call({
                method: "vontoc.api.delivery_note.sent_dn_for_approval",
                args: { docname: frm.doc.name },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
        }

        else if (state === 'Ready For Dispatch') {
            frappe.call({
                method: "vontoc.api.delivery_note.approve_dn",
                args: { docname: frm.doc.name },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
        }

        else if (state === 'Return Required') {
            frappe.call({
                method: "vontoc.api.delivery_note.require_return",
                args: { docname: frm.doc.name },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
        }
        //è¡¨å•æäº¤é€»è¾‘åœ¨class on submité‡Œé¢è°ƒç”¨
    }
});

frappe.ui.form.on('Delivery Note', {
    refresh: function(frm) {
        frappe.call({
            method: "vontoc.utils.utils.get_user_profile_name", // å‡è®¾è¿”å›žå½“å‰ç”¨æˆ·è§’è‰²
            callback: function(r) {
                if (
                    r.message === "delivery" &&  // ä½ è¿”å›žçš„è§’è‰²å
                    !frm.doc.custom_packing_confirmed &&    // è‡ªå®šä¹‰å­—æ®µåˆ¤æ–­
                    frm.doc.workflow_state === "Ready For Dispatch"
                ) {
                    frm.add_custom_button("å®Œæˆè£…ç®±å•", () => {
                        frappe.call({
                            method: "vontoc.api.delivery_note.mark_ready_for_dispatch",
                            args: {
                                docname: frm.doc.name
                            },
                            callback: function() {
                                frm.reload_doc();
                            }
                        });
                    });
                }
            }
        });
    }
});

frappe.ui.form.on('Delivery Note', {
    refresh: function(frm) {
        frappe.call({
            method: "vontoc.utils.utils.get_user_profile_name",
            callback: function(r) {
                const isDeliveryUser = r.message === "delivery";
                const notDispatched = !frm.doc.custom_delivery_dispatched;
                const notReturned = !frm.doc.is_return;
                const isSubmitted = frm.doc.docstatus === 1;
                if (isDeliveryUser && notDispatched && notReturned && isSubmitted) {
                    frm.add_custom_button("å‘è´§å®Œæˆ", () => {
                        frappe.call({
                            method: "vontoc.api.delivery_note.delivery_dispatched",
                            args: {
                                docname: frm.doc.name
                            },
                            callback: function() {
                                frm.reload_doc();
                            }
                        });
                    });
                }
            }
        });
    }
});

frappe.ui.form.on('Supplier Quotation', {
    refresh: function(frm) {
        frm.add_custom_button(__('Create Guideline Price'), function() {
            frappe.call({
                method: "vontoc.api.guideline_price.create_guideline_price",
                args: {
                    supplier_quotation: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.set_route("Form", "Guideline Price", r.message);
                    }
                }
            });
        });
    }
});

frappe.ui.form.on("Purchase Invoice", {
    after_workflow_action: function(frm) {
        if (frm.doc.workflow_state === 'Pending') {
            frappe.call({
                method: "vontoc.api.purchase_invoice.validate_pr",
                args: {
                    docname: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Payment Request', {
    after_workflow_action: function(frm) {
        if (frm.doc.workflow_state === 'Pending') {
            frappe.call({
                method: "vontoc.api.payment_request.validate_pr",
                args: {
                    docname: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__(r.message));
                        frm.reload_doc();
                    }
                }
            });
        }
    }
})
