class CRMDeal {
    async status() {
        await this.doc.trigger('updateProbability')
    }
    async updateProbability() {
        let status = await call("frappe.client.get_value", {
            doctype: "CRM Deal Status",
            fieldname: "probability",
            filters: { name: this.doc.status },
        })

        this.doc.probability = status.probability
    }
}

async function setupForm({ doc, call, $dialog, updateField, toast }) {
	let actions = [];
	let is_erpnext_integration_enabled = await call("frappe.client.get_single_value", {doctype: "ERPNext CRM Settings", field: "enabled"});
	if (!["Lost", "Won"].includes(doc?.status) && is_erpnext_integration_enabled) {
		actions.push({
			label: __("Create Quotation"),
			onClick: async () => {
				let quotation_url = await call(
					"crm.fcrm.doctype.erpnext_crm_settings.erpnext_crm_settings.get_quotation_url", 
					{
						crm_deal: doc.name,
						organization: doc.organization
					}
				);

				if (quotation_url) {
					window.open(quotation_url, '_blank');
				}
			}
		})
	}
	if (is_erpnext_integration_enabled) {
		let customer_url = await call("crm.fcrm.doctype.erpnext_crm_settings.erpnext_crm_settings.get_customer_link", {
			crm_deal: doc.name
		});
		if (customer_url) {
			actions.push({
				label: __("View Customer"),
				onClick: () => window.open(customer_url, '_blank')
			});
		}
	}
	return {
		actions: actions,
	};
}

async function setupForm({ doc, call, $dialog, updateField, toast }) {
    let actions = [];

    // Previous Quotation 按钮
    actions.push({
        label: "Previous Quotation",
        onClick: () => {
            const url = `/app/quotation?party_name=${encodeURIComponent(doc.name)}`;
            window.open(url, "_blank");
        }
    });

    return { actions };
}

async function setupForm({ doc }) {
    let actions = [];

    // 仅当状态不是 "Won" 时显示按钮
    if (doc.status !== "Won") {
        actions.push({
            label: "Create Price List",
            onClick: () => {
                // 构造 URL，带上默认字段值
                const url = `/app/quotation-price-list/new`
                    + `?quotation_to=${encodeURIComponent("CRM Deal")}`
                    + `&party_name=${encodeURIComponent(doc.name)}`;

                window.open(url, "_blank");
            }
        });
    }

    return { actions };
}

