{% set company = None %}

{% if doc.company %}
    {% set company = frappe.get_doc('Company', doc.company) %}
{% endif %}

{% if company %}
<div class="row">
    <div class="column-break col-xs-6">
        {% if company.company_logo: %}
            <img src="{{ company.company_logo }}" style="width: 120px;">
        {% endif %}
    </div>
    <div class="column-break col-xs-6">
        <div class="row">
            <b>{{ doc.company }}</b>
        </div>
    {% set companyAdress = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Company', 'link_name': doc.company, 'parenttype': 'Address'}, fields=['parent']) %}
    {% if companyAdress %}
        {% for adrName in companyAdress %}
            {% set companyAdr = frappe.get_doc("Address", adrName.parent) %}
                {% if companyAdr.is_your_company_address %}
    <div class="row">
        {{ companyAdr.address_line1 }}
    </div>
                {% if companyAdr.address_line2 %}
                <div class="row">
                    {{ companyAdr.address_line2 }}
                </div>
                {% endif %}
    <div class="row">
        {{ companyAdr.pincode }} {{ companyAdr.city }}
    </div>
    <div class="row">
        {{ companyAdr.country }}
    </div>
                {% endif %}
        {% endfor %}
    {% endif %}
    <div class="row"></div>
</div>
{% endif %}</div>

footer:
{% set company = None %}

{% if doc.company %}
    {% set company = frappe.get_doc('Company', doc.company) %}
{% endif %}

{% if company %}

    {% set bank_account = frappe.db.get_list('Bank Account', fields=['iban', 'swift_number'], filters={'company': company.name, 'is_company_account': 1 }, page_length=1) %}
    {% if company.legal_form: %}
        {% set legal_form = frappe.get_doc('Legal Form', company.legal_form) %}
    {% endif %}

<div class="border-top" style="width: 100%;"></div>
<span class="small text-center">
    {% if company.siret: %}
        {{ _("Siret") }}: {{ company.siret }},
    {% endif %}
    {% if company.tax_id: %}
        {{ _("Intracom Number") }}: {{ company.tax_id }},
    {% endif %}
    {% if legal_form and legal_form.label: %}
        {{ _("Legal Form") }}: {{ legal_form.label }},
    {% endif %}
    {% if company.eori_number: %}
        {{ _("EORI Number") }}: {{ company.eori_number }}
    {% endif %}
    {% if bank_account and bank_account[0].iban: %}
        {{ _("IBAN") }}: {{ bank_account[0].iban }}
    {% endif %}
    {% if bank_account and bank_account[0].swift_number: %}
        {{ _("Swift Number") }}: {{ bank_account[0].swift_number }}
    {% endif %}                                         
</span>

{% endif %}
