css:
.doc-type-title {
font-size: 20pt;
font-weight: 900; 
color: #999999;
}

.doc-name {
font-size: 14pt;
font-weight: 600;
color: #999999;
}

div[data-fieldname="grand_total"],
div[data-fieldname="discount_amount"],
div[data-fieldname="total_qty"]
{
  position: relative;
}

div[data-fieldname="grand_total"] .value,
div[data-fieldname="discount_amount"] .value,
div[data-fieldname="total_qty"] .value
{
    position: absolute; right: 0px; text-align: right;
}

.print-format [data-fieldname="payment_schedule"] table,
.print-format [data-fieldname="payment_schedule"] th,
.print-format [data-fieldname="payment_schedule"] td {
    background: transparent; /* 浅灰半透明 */
    box-shadow: none ;
    border: none ;
    border-bottom: 1px solid #e0e0e0 ; /* 只保留下边框 */
}


.print-format [data-fieldname="payment_schedule"] table {
    margin-top: 0;
}
