css:
.doc-type-title {
font-size: 20pt;
font-weight: 900; 
color: #999999;
}

.doc-name {
font-size: 14pt;
font-weight: 600;
color: #999999;
}

div[data-fieldname="grand_total"],
div[data-fieldname="total"],
div[data-fieldname="total_qty"]
{
  display: flex;
}

div[data-fieldname="grand_total"] .value {
  text-align: right;
}

[data-fieldname="payment_schedule"] table {
    margin: 0 !important;          /* table 外层 margin */
    padding: 0 !important;         /* table 内 padding */
    border-spacing: 0 !important;  /* 去掉单元格间距 */
    border-collapse: collapse !important; /* 合并边框，消除额外空隙 */
}
