css:
.doc-type-title {
font-size: 20pt;
font-weight: 900; 
color: #999999;
}

.doc-name {
font-size: 14pt;
font-weight: 600;
color: #999999;
}

div[data-fieldname="grand_total"],
div[data-fieldname="discount_amount"],
div[data-fieldname="total_qty"]
{
    position: relative;
}

div[data-fieldname="grand_total"] .value,
div[data-fieldname="discount_amount"] .value,
div[data-fieldname="total_qty"] .value
{
    position: absolute; right: 0px; text-align: right;
}

div[data-fieldname="address_display"] :first-child {
    display: none;
}

div[data-fieldname="grand_total"] .value,
div[data-fieldname="discount_amount"] .value {
  text-align: right ;
}

div[data-fieldname="grand_total"] .value {
  padding-right: 0;
}

div[data-fieldname="grand_total"] {
    margin-right: 0px;
    padding-right: 0px;
}

div[data-fieldname="company"] .value
{
    font-weight:bold;
}

div[data-fieldname="tax_id"],
div[data-fieldname="due_date"]
{
    display:flex;
    margin-top:0;
}

[data-fieldname="address_display"] .value br:last-child,
[data-fieldname="address_display"] .value br:nth-last-child(2) {
    display: none;
}

div[data-fieldname="address_display"]
{
    margin-bottom:0;
}

[data-fieldname="tax_id"] > div:first-child {
    display: none;
}
